import{e as s,g as d}from"./chunk-I5YFABJ7.js";import"./chunk-Y5IR77AS.js";import{Ab as i,Wa as l,_b as e,bb as o,zb as t}from"./chunk-7ZUF3SYJ.js";var m=class r{constructor(n){this.activatedRoute=n}ngOnInit(){this.activatedRoute.fragment.subscribe(n=>{n&&this.jumpToSection(n)})}jumpToSection(n){n&&document.getElementById(n)?.scrollIntoView({behavior:"smooth"})}static \u0275fac=function(a){return new(a||r)(l(s))};static \u0275cmp=o({type:r,selectors:[["app-personal-combat"]],decls:1235,vars:0,consts:[["id","top"],[1,"lead"],["id","range"],[1,"table","table-bordered","table-striped","table-hover","table-responsive-md"],[1,"thead-light"],[1,"table-warning"],[1,"bg-warning"],[1,"table-danger"],[1,"bg-danger"],["id","starting-range"],["id","initiative"],["routerLink","/book1/skills","fragment","tactics"],["id","combat-round"],["id","dynamic-initiative"],["id","minor-actions"],["id","aiming"],["id","aiming-for-the-kill"],["id","changing-stance"],["id","drawing-and-reloading"],["id","movement"],["id","miscellaneous"],["id","significant-actions"],["id","attack"],[1,"list-unstyled","mb-0"],["id","coup-de-grace"],["id","reactions"],["id","dodging"],["id","parrying"],["id","other-actions"],["id","free-actions"],["id","extended-actions"],["id","delay"],["id","special-considerations"],["id","battlefield-comms"],["id","battlefield-conditions"],["id","battlefield-sensors"],["id","blind-firing"],["id","burst-fire"],["id","cover"],["id","explosions"],["id","extreme-range-firing"],["routerLink","/book1/personalcombat","fragment","aiming-for-the-kill"],["id","fighting-in-zero-gravity"],["routerLink","/book1/skills","fragment","zero-g"],["id","firing-into-combat"],["id","grappling"],["routerLink","/book1/skills","fragment","natural-weapons"],["id","panic-fire"],["id","shotgun-spread"],["id","suppression-fire"],["id","stance"],["id","tactics-and-leadership"],["routerLink","/book1/skills","fragment","leadership"],["id","thrown-weapons"],["id","damage"],["id","armor"],["id","fatigue"],["id","unconsciousness"],["id","injury-and-recovery"],["id","natural-healing"],["id","medical-treatment"],["routerLink","/book1/skills","fragment","medicine"],["id","healing-and-mental-characteristics"],["id","vehicles-in-personal-combat"],["id","closed-and-open-vehicles"],["id","vehicle-mounted-weapons"],["id","collisions"],["id","vehicular-actions"],["id","vehicle-damage"],["routerLink","/book1/skills","fragment","electronics"],["routerLink","/book1/skills","fragment","engineering"],["routerLink","/book1/skills","fragment","gravitics"],["routerLink","/book1/skills","fragment","mechanics"],["id","ground-force-weaponry-against-starship-scale-targets"]],template:function(a,c){a&1&&(t(0,"h1",0),e(1,"Chapter 5: Personal Combat"),i(),t(2,"p",1),e(3," Personal combat is a common means of resolving conflicts. This chapter provides detailed rules for personal combat. "),i(),t(4,"h2"),e(5,"Personal Combat Checklist"),i(),t(6,"p"),e(7," Personal combat in the Cepheus Engine is cyclical. Everybody acts in turn in a regular cycle called a round. Generally, combat runs in the following way: "),i(),t(8,"ol")(9,"li"),e(10,"The Referee determines which characters are aware of their opponents at the start of the battle. If some but not all combatants are aware of their opponents, the combatants that are aware of their opponents are considered to get an automatic 12 on their initiative roll, giving them an Initiative of 12 + Dexterity DM. "),i(),t(11,"li"),e(12,"Any remaining combatants roll initiative. All combatants are now ready to begin their first round of combat."),i(),t(13,"li"),e(14,"Combatants act in initiative order."),i(),t(15,"li"),e(16,"When everyone has had a turn, the combatant with the highest initiative total acts again, and steps 4 and 5 repeat until combat ends."),i()(),t(17,"h2",2),e(18,"Range"),i(),t(19,"p"),e(20," Personal combat is divided into a series of range bands. These are identified in the Personal Combat Range Bands table. "),i(),t(21,"table",3)(22,"caption"),e(23,"Table: Personal Combat Range Bands"),i(),t(24,"thead",4)(25,"tr")(26,"th"),e(27,"Range"),i(),t(28,"th"),e(29,"Distance to Target"),i(),t(30,"th"),e(31,"Squares to Target"),i()()(),t(32,"tr")(33,"th"),e(34,"Personal"),i(),t(35,"td"),e(36,"Less than 1.5 meters"),i(),t(37,"td"),e(38,"0 (combatants are in the same square)"),i()(),t(39,"tr")(40,"th"),e(41,"Close"),i(),t(42,"td"),e(43,"1.5 to 3 meters"),i(),t(44,"td"),e(45,"1 to 2 squares"),i()(),t(46,"tr")(47,"th"),e(48,"Short"),i(),t(49,"td"),e(50,"3 to 12 meters"),i(),t(51,"td"),e(52,"3 to 8 squares"),i()(),t(53,"tr")(54,"th"),e(55,"Medium"),i(),t(56,"td"),e(57,"12 to 50 meters"),i(),t(58,"td"),e(59,"9 to 34 squares"),i()(),t(60,"tr")(61,"th"),e(62,"Long"),i(),t(63,"td"),e(64,"51 meters to 250 meters"),i(),t(65,"td"),e(66,"35 to 166 squares"),i()(),t(67,"tr")(68,"th"),e(69,"Very Long"),i(),t(70,"td"),e(71,"251 meters to 500 meters"),i(),t(72,"td"),e(73,"167 to 334 squares"),i()(),t(74,"tr")(75,"th"),e(76,"Distant"),i(),t(77,"td"),e(78,"501 meters+"),i(),t(79,"td"),e(80,"334 squares+"),i()()(),t(81,"p"),e(82," The Difficulty of any attack is based on the weapon type and the range of the attack, as per the Attack Difficulties by Weapon Type table. "),i(),t(83,"table",3)(84,"caption"),e(85,"Table: Personal Combat Range Bands"),i(),t(86,"thead",4)(87,"tr")(88,"th"),e(89,"Weapon"),i(),t(90,"th"),e(91,"Personal"),i(),t(92,"th"),e(93,"Close"),i(),t(94,"th"),e(95,"Short"),i(),t(96,"th"),e(97,"Medium"),i(),t(98,"th"),e(99,"Long"),i(),t(100,"th"),e(101,"Very Long"),i(),t(102,"th"),e(103,"Distant"),i()()(),t(104,"tr")(105,"th"),e(106,"Close Quarters"),i(),t(107,"td",5),e(108,"Average"),i(),t(109,"td",6),e(110,"Difficult"),i(),t(111,"td"),e(112,"\u2014"),i(),t(113,"td"),e(114,"\u2014"),i(),t(115,"td"),e(116,"\u2014"),i(),t(117,"td"),e(118,"\u2014"),i(),t(119,"td"),e(120,"\u2014"),i()(),t(121,"tr")(122,"th"),e(123,"Extended Reach"),i(),t(124,"td",6),e(125,"Difficult"),i(),t(126,"td",5),e(127,"Average"),i(),t(128,"td"),e(129,"\u2014"),i(),t(130,"td"),e(131,"\u2014"),i(),t(132,"td"),e(133,"\u2014"),i(),t(134,"td"),e(135,"\u2014"),i(),t(136,"td"),e(137,"\u2014"),i()(),t(138,"tr")(139,"th"),e(140,"Thrown"),i(),t(141,"td"),e(142,"\u2014"),i(),t(143,"td",5),e(144,"Average"),i(),t(145,"td",6),e(146,"Difficult"),i(),t(147,"td",6),e(148,"Difficult"),i(),t(149,"td"),e(150,"\u2014"),i(),t(151,"td"),e(152,"\u2014"),i(),t(153,"td"),e(154,"\u2014"),i()(),t(155,"tr")(156,"th"),e(157,"Pistol"),i(),t(158,"td",6),e(159,"Difficult"),i(),t(160,"td",5),e(161,"Average"),i(),t(162,"td",5),e(163,"Average"),i(),t(164,"td",6),e(165,"Difficult"),i(),t(166,"td",7),e(167,"Very Difficult"),i(),t(168,"td"),e(169,"\u2014"),i(),t(170,"td"),e(171,"\u2014"),i()(),t(172,"tr")(173,"th"),e(174,"Rifle"),i(),t(175,"td",7),e(176,"Very Difficult"),i(),t(177,"td",6),e(178,"Difficult"),i(),t(179,"td",5),e(180,"Average"),i(),t(181,"td",5),e(182,"Average"),i(),t(183,"td",5),e(184,"Average"),i(),t(185,"td",6),e(186,"Difficult"),i(),t(187,"td",7),e(188,"Very Difficult"),i()(),t(189,"tr")(190,"th"),e(191,"Shotgun"),i(),t(192,"td",6),e(193,"Difficult"),i(),t(194,"td",5),e(195,"Average"),i(),t(196,"td",6),e(197,"Difficult"),i(),t(198,"td",6),e(199,"Difficult"),i(),t(200,"td",7),e(201,"Very Difficult"),i(),t(202,"td"),e(203,"\u2014"),i(),t(204,"td"),e(205,"\u2014"),i()(),t(206,"tr")(207,"th"),e(208,"Assault Weapon"),i(),t(209,"td",6),e(210,"Difficult"),i(),t(211,"td",5),e(212,"Average"),i(),t(213,"td",5),e(214,"Average"),i(),t(215,"td",5),e(216,"Average"),i(),t(217,"td",6),e(218,"Difficult"),i(),t(219,"td",7),e(220,"Very Difficult"),i(),t(221,"td",8),e(222,"Formidable"),i()(),t(223,"tr")(224,"th"),e(225,"Rocket"),i(),t(226,"td",7),e(227,"Very Difficult"),i(),t(228,"td",6),e(229,"Difficult"),i(),t(230,"td",6),e(231,"Difficult"),i(),t(232,"td",5),e(233,"Average"),i(),t(234,"td",5),e(235,"Average"),i(),t(236,"td",6),e(237,"Difficult"),i(),t(238,"td",7),e(239,"Very Difficult"),i()()(),t(240,"h3",9),e(241,"Starting Range"),i(),t(242,"p"),e(243," When a combat begins, the Referee must decide the starting range that separates the groups of combatants. For combat in tight quarters, such as inside an office complex or starship, or within an underground tunnel system, the starting range is usually Short. The range for outdoor encounters is usually Medium, although open areas such as prairies, deserts, open roads and ocean surfaces make it easier to see at a distance, so Long or even Very Long range would not be inappropriate. Total darkness reduces starting range to Short or less. Partial darkness restricts starting range to Medium or less. "),i(),t(244,"h2",10),e(245,"Initiative"),i(),t(246,"p"),e(247," If the combatants are all unprepared for combat, then each rolls 2D6 and adds their Dexterity DM to determine starting Initiative. Initiative determines the order that characters act in, but it can also be spent to react to events. If some of the combatants are ready for combat and some are not, such as in an ambush, the prepared characters are considered to get an automatic 12 on their roll, giving them an Initiative of 12 + Dexterity DM. Characters that are aware of a group, and that group is unaware of them, may elect to avoid conflict entirely. "),i(),t(248,"p"),e(249," Characters that have the "),t(250,"a",11),e(251,"Tactics"),i(),e(252," skill may make a "),t(253,"a",11),e(254,"Tactics"),i(),e(255," check and add the Effect of this check to the Initiative of everyone in their unit with whom they are in direct communication. "),i(),t(256,"h2",12),e(257,"The Combat Round"),i(),t(258,"p"),e(259," Each combat round lasts around six seconds of game time. In a combat round each character gets a minor action and a significant action. Actions are taken in descending order of Initiative. If two characters have the same Initiative, the character with the highest Dexterity goes first. If they are still tied, then characters act simultaneously. When a character acts, he takes all his actions at once. "),i(),t(260,"p"),e(261," Once everyone has acted a combat round is over and a new round begins. Initiative is not re-rolled but is dynamic, and may be adjusted up and down by actions taken during a round. "),i(),t(262,"h3",13),e(263,"Dynamic Initiative"),i(),t(264,"p"),e(265," During the course of a round a character's Initiative score may be changed by reactions and hastening. Any changes affect your Initiative for one round only \u2013 either the current round if you have yet to act or the following round if you have acted already. Reactions reduce your Initiative in order to allow you to defend yourself from attacks. Hastening your action lets you act sooner but at a penalty to your roll. "),i(),t(266,"p"),e(267," At the start of each combat round a character may declare that he is acting hastily. This gives him a +2 bonus to his Initiative for that round only but all his actions receive a \u20131 DM. A character can only hasten once. "),i(),t(268,"h2",14),e(269,"Minor Actions"),i(),t(270,"p"),e(271," Minor actions are actions intended to move a distance or to manipulate or move an object. You can take up to three minor actions per round, at the loss of a significant action. "),i(),t(272,"h3",15),e(273,"Aiming "),i(),t(274,"p"),e(275," A character that spends a minor action aiming at a target gets a +1 DM to his next attack on the target, as long as the character does nothing except aim until he makes his attack. A character may spend multiple actions on aiming, gaining a maximum aiming DM of +6 if he spends six minor actions on aiming. "),i(),t(276,"h3",16),e(277,"Aiming for the Kill"),i(),t(278,"p"),e(279," Specifically aiming for a kill works exactly like regular Aiming, but does not add to the character's DM to hit the target. Instead, the character gains a bonus of +2 to his damage equal to the number of minor actions he spends Aiming for the Kill. He cannot dodge, duck, or move while aiming. He also loses his Aim bonus if hit or distracted. The maximum bonus obtainable from Aiming for the Kill is +6. "),i(),t(280,"h3",17),e(281,"Changing Stance"),i(),t(282,"p"),e(283," A character can change to any one of the three stances \u2013 prone, crouched or standing \u2013 as a minor action. "),i(),t(284,"h3",18),e(285,"Drawing and Reloading"),i(),t(286,"p"),e(287," The time taken to draw a weapon depends on its size and ease of use. The number of minor actions to ready or reload a weapon is listed in the description of each weapon. Most weapons take one minor action to draw and another minor action to reload, but some weapons are especially fast or slow. "),i(),t(288,"h3",19),e(289,"Movement"),i(),t(290,"p"),e(291," The character moves up to six meters. When using a square grid for modelling the combat, each square should measure 1.5 meters to the side. This means that the average character moves four squares on his turn. Difficult terrain, such as rubble, mud or thick under-brush can halve a character's movement, allowing him to move only three meters per movement action. Crouching also halves movement. "),i(),t(292,"h3",20),e(293,"Miscellaneous"),i(),t(294,"P"),e(295," The Referee may permit a character to perform a skill check or other action as a minor action if the use of the skill does not require the character's full attention or complex physical actions. "),i(),t(296,"h2",21),e(297,"Significant Actions"),i(),t(298,"P"),e(299," Significant actions are intended to do something within about 3 seconds. You can perform a single significant action per round, or forego it to perform a total of three minor actions. "),i(),t(300,"h3"),e(301,"Minor Actions"),i(),t(302,"p"),e(303," A character can take two minor actions instead of a significant action. "),i(),t(304,"h3"),e(305,"Miscellaneous"),i(),t(306,"p"),e(307," A character may make a skill check or do something else as a significant action when such an action requires the character's full attention, concentration, complicated physical actions or some combination thereof. Any skill check with a time interval of 1-6 seconds is simply considered a significant action during combat. "),i(),t(308,"h3",22),e(309,"Attack"),i(),t(310,"P"),e(311," The most common significant action is an attack. The basic attack action is trying to injure a foe with a melee attack or a ranged weapon. The attacker declares his target, and the foe may choose to react. The attacker then makes a skill check, and if successful, deals damage to his target. The actual Difficulty of this skill check is determined using the weapon type and the range of the attack, as given earlier in this chapter under Range. "),i(),t(312,"p"),e(313," The standard skill checks used in making an attack are: "),i(),t(314,"dl")(315,"dt"),e(316,"Melee Attack"),i(),t(317,"dd"),e(318,"2D6 + appropriate melee combat skill + Strength or Dexterity DM (attacker's choice)"),i(),t(319,"dt"),e(320,"Shooting Attack"),i(),t(321,"dd"),e(322,"2D6 + appropriate gun combat skill + Dexterity DM"),i(),t(323,"dt"),e(324,"Thrown Attack "),i(),t(325,"dd"),e(326,"2D6 + Athletics + Dexterity DM"),i()(),t(327,"table",3)(328,"caption"),e(329,"Table: Common Modifiers to Attacks in Personal Combat"),i(),t(330,"thead",4)(331,"tr")(332,"th"),e(333,"Action"),i(),t(334,"th"),e(335,"Modifier"),i()()(),t(336,"tr")(337,"th"),e(338,"Aiming"),i(),t(339,"td"),e(340,"+1 per Aim action"),i()(),t(341,"tr")(342,"th"),e(343,"Cover"),i(),t(344,"td"),e(345,"\u20130 to \u20136"),i()(),t(346,"tr")(347,"th"),e(348,"Environmental Effects"),i(),t(349,"td"),e(350,"\u20131 to \u20132"),i()(),t(351,"tr")(352,"th"),e(353,"Intelligent Weapon"),i(),t(354,"td"),e(355,"+1 if total DM is within the program's tolerance"),i()(),t(356,"tr")(357,"th"),e(358,"Laser Sight"),i(),t(359,"td"),e(360,"+1 if aiming"),i()(),t(361,"tr")(362,"th"),e(363,"Movement"),i(),t(364,"td"),e(365,"\u20131 for every 10 full meters of target movement"),i()(),t(366,"tr")(367,"th"),e(368,"Target Dodges (Reaction)"),i(),t(369,"td"),e(370,"\u20131"),i()(),t(371,"tr")(372,"th"),e(373,"Target Parries (Reaction)"),i(),t(374,"td"),e(375,"\u2013Defender's appropriate melee skill"),i()(),t(376,"tr")(377,"th"),e(378,"Target Stance"),i(),t(379,"td")(380,"ul",23)(381,"li"),e(382,"\u20132 if attacking a prone target at Medium or greater range"),i(),t(383,"li"),e(384,"+2 if attacking a prone target at Personal range"),i()()()(),t(385,"tr")(386,"th"),e(387,"Weapons with Recoil (in Zero Gravity)"),i(),t(388,"td"),e(389,"\u20132"),i()()(),t(390,"h3",24),e(391,"Coup de Grace"),i(),t(392,"p"),e(393," A character can use a melee weapon to deliver a coup de grace to a helpless opponent that is within close range. They can also use a ranged weapon to do so, provided they are adjacent to the target. The attack automatically hit and the opponent dies. "),i(),t(394,"h2",25),e(395,"Reactions"),i(),t(396,"p"),e(397," Reactions are actions taken immediately in response to the action of another. The more time a character spends reacting, the longer it will be until he acts himself. Each reaction lowers Initiative by 2 and applies a \u20131 DM to all skill checks until the following round. There is no limit to how many times a character can react in a round but a character can only react once to each attack and the penalties from reacting are cumulative. "),i(),t(398,"p"),e(399," A character can only react to attacks that he is aware of. "),i(),t(400,"h3",26),e(401,"Dodging"),i(),t(402,"p"),e(403," A character who is being attacked may dodge, giving his attacker a \u20131 DM and giving himself a \u20131 DM on all skill checks until the next round. If the character is in cover or has an obstruction to duck or dodge behind, the DM to hit him is increased to \u20132. "),i(),t(404,"h3",27),e(405,"Parrying"),i(),t(406,"p"),e(407," A character who is being attacked in melee can parry, applying his Melee skill as a negative DM equal to the attack roll. A parrying character also has a \u20131 DM on all skill checks until the next round. "),i(),t(408,"h2",28),e(409,"Other Actions"),i(),t(410,"p"),e(411," Other types of action can take place during personal combat. "),i(),t(412,"h3",29),e(413,"Free Actions"),i(),t(414,"p"),e(415," Some actions are so fast they do not even qualify as a minor action \u2013 shouting a warning, pushing a button, checking your watch, and so on. A character can perform as many of these free actions as he likes in a turn, although if he performs several the Referee may require him to spend a minor or even a significant action on his various tasks. "),i(),t(416,"h3",30),e(417,"Extended Actions"),i(),t(418,"p"),e(419," Some skill checks will take longer than a single combat round to complete. Make a Timing roll for the task and then work out how many six second combat rounds it will take to complete. A character engaging in an extended action cannot do anything else but can abandon their action at any time and return to the normal Initiative order. A character who is hit by an attack while undertaking an extended action must make an 8+ roll using the skill in question with a negative DM equal to the amount of damage the attack causes (after armor). Failure indicates that this round's work does not count towards the completion of the task. Failure by six or more (an Exceptional Failure) ruins the task and the character must start again. "),i(),t(420,"h3",31),e(421,"Delay"),i(),t(422,"p"),e(423," A character does not have to act when his turn comes up in the Initiative order. He may act at any later point during the round, even interrupting another's actions to do so. When he acts, his Initiative is set to the count on which he acted. If the character has not acted by the end of the round he may choose to act first in the next round, effectively giving up his actions in the previous round in exchange for an Initiative advantage. His new Initiative is set to one higher than that of the current first person in the order. When multiple characters are delaying and all wish to act first in the following round, their Initiatives are all set to the same score and they act in Dexterity order as normal. "),i(),t(424,"h2",32),e(425,"Special Considerations"),i(),t(426,"p"),e(427," The following are special considerations in Personal Combat. "),i(),t(428,"h3",33),e(429,"Battlefield Comms"),i(),t(430,"p"),e(431," Communications technology is a vital part of the battlefield. If a character is not in communication with the rest of his unit and his commander, then he cannot benefit from Tactics or Leadership. Characters that benefitted from Tactics at the start of combat and are later cut off from their commander have their Initiative lowered by the same amount it was boosted at the start of combat. Unlike other Initiative modifications, this lasts until combat ends or communication is re-established. There are several methods of communication: "),i(),t(432,"dl")(433,"dt"),e(434,"Direct"),i(),t(435,"dd"),e(436,"This covers hand signals and verbal communications."),i(),t(437,"dt"),e(438,"Hardlinks"),i(),t(439,"dd"),e(440,"Hardlinks are wires or other physical connections, and cannot be jammed."),i(),t(441,"dt"),e(442,"Radio"),i(),t(443,"dd"),e(444,"Radio communications allow communications as long as the radio signal can get through \u2013 they can be jammed or blocked by local conditions."),i(),t(445,"dt"),e(446,"Laser"),i(),t(447,"dd"),e(448,"Two characters with tight beam lasers are in communication as long as line of sight exists between a character and another friendly laser-comm equipped character."),i(),t(449,"dt"),e(450,"Masers"),i(),t(451,"dd"),e(452,"These work just like lasers, but can cut through smoke and aerosols."),i(),t(453,"dt"),e(454,"Meson"),i(),t(455,"dd"),e(456,"Meson communicators cannot be jammed or blocked, but cannot be used while a character is moving."),i()(),t(457,"h3",34),e(458,"Battlefield Conditions"),i(),t(459,"p"),e(460," Certain battlefield conditions affect ranged attacks: "),i(),t(461,"dl")(462,"dt"),e(463,"Darkness"),i(),t(464,"dd"),e(465,"Low light gives a \u20131 DM to ranged attacks. Complete darkness gives a \u20134 DM. Light penalties can be avoided by using sensors to target instead of the naked eye."),i(),t(466,"dt"),e(467,"Smoke or Fog"),i(),t(468,"dd"),e(469,"Smoke gives a \u20131 DM to ranged attacks by obscuring the target; especially thick and impenetrable smoke gives a \u20132 DM. These penalties are doubled for laser weapons."),i(),t(470,"dt"),e(471,"Extreme Weather"),i(),t(472,"dd"),e(473,"Driving wind, rain, snowstorms and so forth give a \u20131 DM to ranged attacks from poor visibility and a \u20131 DM to ranged attacks from environmental interference. Sensors can be used to avoid the visibility penalty."),i()(),t(474,"h3",35),e(475,"Battlefield Sensors"),i(),t(476,"p"),e(477," There are several types of sensors. "),i(),t(478,"dl")(479,"dt"),e(480,"Bioscanner"),i(),t(481,"dd"),e(482,"Bioscanner 'sniffers' detect airborne pathogens and hazardous chemicals."),i(),t(483,"dt"),e(484,"Infra-Red (Heat)"),i(),t(485,"dd"),e(486,"Infra-red sensors detect warm bodies, and negate concealment from smoke and soft cover, but can be jammed by strong heat sources."),i(),t(487,"dt"),e(488,"Densitometer"),i(),t(489,"dd"),e(490,"An outgrowth of gravitic technology, a densitometer can scan an area and plot variable densities, effectively creating a three-dimensional map of all objects."),i(),t(491,"dt"),e(492,"Electromagnetic Detectors"),i(),t(493,"dd"),e(494,"These sensors can detect unshielded high-power electrical devices, such as gauss weapons or transmitters."),i(),t(495,"dt"),e(496,"Laser-Assisted Targeting"),i(),t(497,"dd"),e(498,"A low-powered laser is reflected off the target, giving targeting data to the firer."),i(),t(499,"dt"),e(500,"Light Intensification"),i(),t(501,"dd"),e(502,"Light intensification technology magnifies visible light, negating the penalties for darkness or low light."),i(),t(503,"dt"),e(504,"Motion Sensors"),i(),t(505,"dd"),e(506,"Can detect motion within range."),i(),t(507,"dt"),e(508,"Neural Activity Sensor (NAS)"),i(),t(509,"dd"),e(510,"A combination of highly sensitive EM-detectors and psionic theory, NAS detectors pick up on the brain activity of living beings and classify them according to amount and complexity, giving a rough idea of the intelligence of subjects."),i()(),t(511,"h3",36),e(512,"Blind Firing"),i(),t(513,"p"),e(514," Blind firing works exactly like any other form of firing (including automatic), but it always treats the firer as having Level 0 in that weapon's relative skill. Additionally an extra die is thrown when firing, but before any calculations are made the highest die (or one of the highest, in case of a tie) is removed completely. Any successful attack(s) needs to then have a die rolled to randomly choose which eligible target in the firing line is hit \u2013 whether friend or foe. "),i(),t(515,"h3",37),e(516,"Burst Fire"),i(),t(517,"p"),e(518," Weapons that fire a burst of 2, 3, 4, 10, 20, or 100 rounds may improve the attacker's odds of either hitting a target or inflict additional damage. The attacker must decide whether to spray the target, increasing the chances of scoring a hit, or to fire a precise, grouped burst in the hope of inflicting greater damage. Either sort of burst fire is considered a significant action. "),i(),t(519,"table",3)(520,"caption"),e(521,"Table: Burst Fire Effects"),i(),t(522,"thead",4)(523,"tr")(524,"th"),e(525,"Burst Size"),i(),t(526,"th"),e(527,"Attack DM"),i(),t(528,"th"),e(529,"Extra Damage"),i()()(),t(530,"tr")(531,"th"),e(532,"1 round"),i(),t(533,"td"),e(534,"+0"),i(),t(535,"td"),e(536,"+0 damage"),i()(),t(537,"tr")(538,"th"),e(539,"3 round burst"),i(),t(540,"td"),e(541,"+1"),i(),t(542,"td"),e(543,"+1 point of damage"),i()(),t(544,"tr")(545,"th"),e(546,"4 round burst"),i(),t(547,"td"),e(548,"+1"),i(),t(549,"td"),e(550,"+1D6 damage"),i()(),t(551,"tr")(552,"th"),e(553,"10 round burst"),i(),t(554,"td"),e(555,"+2"),i(),t(556,"td"),e(557,"+2D6 damage"),i()(),t(558,"tr")(559,"th"),e(560,"20 round burst"),i(),t(561,"td"),e(562,"+3"),i(),t(563,"td"),e(564,"+3D6 damage"),i()(),t(565,"tr")(566,"th"),e(567,"100 round burst"),i(),t(568,"td"),e(569,"+4"),i(),t(570,"td"),e(571,"+4D6 damage"),i()()(),t(572,"h3",38),e(573,"Cover"),i(),t(574,"p"),e(575," Any sort of low wall, undergrowth, convenient rocks or other objects can serve as cover. Attacks made on characters who are behind cover suffer the negative cover DM on the Cover Modifiers table below. Crouching or prone targets can claim cover one step higher on the table. If a character in full cover is crouching or prone they are impossible to hit but cannot return fire. "),i(),t(576,"table",3)(577,"caption"),e(578,"Table: Cover Modifiers"),i(),t(579,"thead",4)(580,"tr")(581,"th"),e(582,"Cover"),i(),t(583,"th"),e(584,"Cover DM"),i()()(),t(585,"tr")(586,"th"),e(587,"1/4 (undergrowth, small rock, corner of a building)"),i(),t(588,"td"),e(589,"\u20130"),i()(),t(590,"tr")(591,"th"),e(592,"1/2 (thick forest, low wall, crate)"),i(),t(593,"td"),e(594,"\u20131"),i()(),t(595,"tr")(596,"th"),e(597,"3/4 (jungle, trench, reinforced position)"),i(),t(598,"td"),e(599,"\u20132"),i()(),t(600,"tr")(601,"th"),e(602,"Full (pillbox)"),i(),t(603,"td"),e(604,"\u20134"),i()()(),t(605,"h3",39),e(606,"Explosions"),i(),t(607,"p"),e(608," Grenades, rockets and other explosives affect an area. A character caught in an explosion may dodge at the usual Initiative cost. A character that dodges an explosion may reduce the damage by 1D6 if he just dodges or by half if he dives for cover. A character that dives for cover ends up prone and loses his next significant action. "),i(),t(609,"h3",40),e(610,"Extreme Range Firing"),i(),t(611,"p"),e(612," Any weapon that has the ability to fire into the Distant range band (including ranged(Rifle), ranged(Assault Weapon) and ranged(Rocket) attacks) can potentially strike targets even beyond, so long as the firer can see the target. Weapons fired at this extreme range do so using the modifiers for Distant range with an additional DM of \u20132. Only characters with at least three levels of skill in the weapon may fire at extreme range. To fire at extreme range, characters must be stationary and prepared to fire from some kind of rest (tripod, tree limb, bunker embrasure and so on). Vehicles must be stationary for weapons to be fired at extreme range from it. Energy-based weapons (lasers, PGMPs, and so on) inflict half damage (round up) at this extreme range. "),i(),t(613,"p"),e(614," This can be combined with "),t(615,"a",41),e(616,"Aiming for the Kill"),i(),e(617," (above). "),i(),t(618,"h3",42),e(619,"Fighting in Zero Gravity"),i(),t(620,"p"),e(621," When fighting in zero gravity, a character's skill levels are limited to lower of the character's "),t(622,"a",43),e(623,"Zero-G"),i(),e(624," skill and the appropriate combat skill. For example, if a character has Laser Pistol-2 and Zero-G-1, then the character shoots with an effective skill level of 1, since his "),t(625,"a",43),e(626,"Zero-G"),i(),e(627," skill level is lower than his Laser Pistol skill level. Characters without "),t(628,"a",43),e(629,"Zero-G"),i(),e(630," skill are treated as unskilled in their attack, because of the disorientation. In addition, characters using weapons with recoil suffer a DM -2 on attack rolls when fighting in zero gravity conditions. "),i(),t(631,"h3",44),e(632,"Firing into Combat"),i(),t(633,"p"),e(634," If a character is firing a weapon at a target that is at Personal range to another combatant, then the attack suffers a \u20132 DM. If the attack misses, roll 1D6. On a 4+, the attack hits the nearest other combatant to the original target. "),i(),t(635,"h3",45),e(636,"Grappling"),i(),t(637,"p"),e(638," A character can attempt to wrestle or grab another person instead of hitting him. The attacker must move to Personal range and beat his target in an opposed "),t(639,"a",46),e(640,"Natural Weapons"),i(),e(641," skill check. If he wins, he may do any one of the following: "),i(),t(642,"ul")(643,"li"),e(644,"Continue the grapple with no other effects."),i(),t(645,"li"),e(646,"Disarm his opponent. If he succeeds by 6+ he can take the weapon away; otherwise it ends up on the floor."),i(),t(647,"li"),e(648,"Drag his opponent up to three meters."),i(),t(649,"li"),e(650,"Escape the grapple and move away (as if with a normal movement action)."),i(),t(651,"li"),e(652,"Inflict damage equal to 2 + the Effect."),i(),t(653,"li"),e(654,"Knock his opponent prone."),i(),t(655,"li"),e(656,"Throw his opponent up to three meters for 1D6 damage."),i()(),t(657,"p"),e(658," Throwing an opponent always ends the grapple. With any other option the winner can choose to end or continue the grapple as he sees fit. A character in a grapple cannot move nor do anything other than make opposed "),t(659,"a",46),e(660,"Natural Weapons"),i(),e(661," skill checks. Each time an opposed check is made the winner can choose an option from the above list. "),i(),t(662,"h3",47),e(663,"Panic Fire"),i(),t(664,"p"),e(665," In order to call upon Panic Fire, a character must be using some form of small arms slug thrower. Panic fire uses all remaining rounds in the weapon, and hits are resolved as if the weapon were being fired using the Burst Fire rules for damage (not for accuracy). There is a DM \u20132 penalty to hit. "),i(),t(666,"h3",48),e(667,"Shotgun Spread"),i(),t(668,"p"),e(669," Any shotgun loaded specifically with flechette rounds can be fired like a common round at a single target up to Short range, using the standard statistics for the weapon. If firing at a target or group of targets at Medium or Long range, the frag shell has its damage reduced to 2d6 but gains a DM+1 bonus to hit, and can hit not only the target aimed at but also anyone (friend or foe) in Personal range with the initial target. "),i(),t(670,"h3",49),e(671,"Suppression Fire"),i(),t(672,"p"),e(673," Suppression fire works by having the character actually target the areas surrounding the target; a wall, a tree or even the ground at their feet are all perfectly suitable. The impact of the character's attacks are often enough to give pause to a potential attacker. The firing character rolls his shooting attack as normal, except with a \u20132 DM for trying to hit cover and nearby objects to the target(s). This attack action also uses up double the normal amount of ammunition per attack. Failures are treated as normal misses. Success means that the firer has hit close enough to the target to force them to duck away, stalling their next action by adding an initiative penalty equal to the Effect of the attack. Suppressed targets also receive a DM-1 penalty to any skill checks that they try to perform in both the current and following combat round. "),i(),t(674,"p"),e(675," Automatic fire can be used for Suppression fire, but no target can be affected twice by Suppression fire on the same action. The target must be allowed to take one action before he can be suppressed again. If multiple hits are used upon the same target, the highest Effect takes precedence. "),i(),t(676,"p"),e(677," There are some situations and instances that can make a target so unshakeable that they do not care about Suppression fire, and will not be harried by it. Some of those exceptions are found on the list below. "),i(),t(678,"ul")(679,"li"),e(680,"Vehicles, or targets fully enclosed in vehicles"),i(),t(681,"li"),e(682,"Zealots"),i(),t(683,"li"),e(684,"Mechanical or android targets"),i(),t(685,"li"),e(686,"Targets wearing full Battle Dress"),i(),t(687,"li"),e(688,"Suicidal targets"),i()(),t(689,"h3",50),e(690,"Stance"),i(),t(691,"p"),e(692," A character can be standing, crouched or prone. "),i(),t(693,"dl")(694,"dt"),e(695,"Standing"),i(),t(696,"dd"),e(697,"A standing character uses the normal rules."),i(),t(698,"dt"),e(699,"Crouching"),i(),t(700,"dd"),e(701,"A crouching character moves at half speed but can make better use of cover. If a crouching character is in cover, consider it one row lower on the Cover Modifiers table. For example, if a character is crouching behind a waist-high wall, he is considered to be at 3/4 cover rather than 1/2 cover."),i(),t(702,"dt"),e(703,"Prone"),i(),t(704,"dd"),e(705,"A prone character cannot make melee attacks or dodge. He may make improved use of cover like a crouching character and he may still parry melee attacks. All ranged attacks targeting him suffer a \u20132 DM penalty. At Close range, the penalty is reduced to +0; a prone character being attacked at Personal range grants a +2 DM to attacks against him."),i()(),t(706,"h3",51),e(707,"Tactics and Leadership"),i(),t(708,"p"),e(709," The "),t(710,"a",11),e(711,"Tactics"),i(),e(712," skill can be used to give an Initiative bonus to a whole unit at the start of combat. The unit commander may make a "),t(713,"a",11),e(714,"Tactics"),i(),e(715," check, and everyone in the unit may increase their Initiative by the Effect of the check. "),i(),t(716,"p"),e(717," The "),t(718,"a",52),e(719,"Leadership"),i(),e(720," skill can be used to increase another character's Initiative. The character with "),t(721,"a",52),e(722,"Leadership"),i(),e(723," makes a "),t(724,"a",52),e(725,"Leadership"),i(),e(726," check, and the target character's Initiative is increased by the Effect of the check. Making a "),t(727,"a",52),e(728,"Leadership"),i(),e(729," skill check is a significant action. "),i(),t(730,"h3",53),e(731,"Thrown Weapons"),i(),t(732,"p"),e(733," There are two kinds of thrown weapons: the first kind strike a single target and do damage from the force of their impact, such as throwing knives or a thrown rock. These use the normal rules for ranged combat. The other kind of thrown weapon is a grenade or other explosive projectile that inflicts no damage from impact but typically delivers a harmful payload. The first kind of thrown weapon adds the Effect of the Athletics check to its damage. The second does not. "),i(),t(734,"p"),e(735," If the attack fails the projectile scatters in a random direction for (6 + Effect) meters. This is usually only important if the projectile explodes on or after impact. "),i(),t(736,"h2",54),e(737,"Damage"),i(),t(738,"p"),e(739," Each weapon lists the damage it inflicts as a number of D6. Add the Effect of the attack roll to this damage. "),i(),t(740,"p"),e(741," The first time a character takes damage, it is applied to the target's Endurance. If a target is reduced to Endurance 0, then further damage is subtracted from the target's Strength or Dexterity (of the character's choice). If either Strength or Dexterity is reduced to 0, the character is unconscious and any further damage is subtracted from the remaining physical characteristic. If all three physical characteristics are reduced to 0, the character is killed. "),i(),t(742,"p"),e(743," Once a character has taken damage, any damage from subsequent attacks is allocated to any of the three physical characteristics as the character so chooses. "),i(),t(744,"table",3)(745,"caption"),e(746,"Table: Damage Results"),i(),t(747,"thead",4)(748,"tr")(749,"th"),e(750,"Characteristic Damage"),i(),t(751,"th"),e(752,"Result"),i()()(),t(753,"tr")(754,"th"),e(755,"At least 1 characteristic is damaged"),i(),t(756,"td"),e(757,"Character is wounded"),i()(),t(758,"tr")(759,"th"),e(760,"All 3 characteristics are damaged"),i(),t(761,"td"),e(762,"Character is seriously wounded"),i()(),t(763,"tr")(764,"th"),e(765,"2 characteristics are at Zero"),i(),t(766,"td"),e(767,"Character is unconscious"),i()(),t(768,"tr")(769,"th"),e(770,"3 characteristics are at Zero"),i(),t(771,"td"),e(772,"Character is dead"),i()()(),t(773,"h3",55),e(774,"Armor"),i(),t(775,"p"),e(776," Armor reduces damage by the value of the armor. A hit with Effect 6+ always inflicts at least one point of damage, regardless of the target's armor. "),i(),t(777,"h3",56),e(778,"Fatigue"),i(),t(779,"p"),e(780," A character can become fatigued in numerous ways, such as staying awake too long, over-exerting themselves or even using certain medications. A fatigued character suffers a \u20132 DM to all checks until he rests. The amount of rest needed is 3 \u2013 the character's Endurance DM hours. If a character suffers fatigue while already fatigued they fall unconscious. "),i(),t(781,"h3",57),e(782,"Unconsciousness"),i(),t(783,"p"),e(784," An unconscious character may make an Endurance check after every minute of unconsciousness \u2013 if successful, he regains consciousness. If he fails he must wait another minute and can then try again with a +1 DM on the check for every check previously failed. "),i(),t(785,"h2",58),e(786,"Injury and Recovery"),i(),t(787,"p"),e(788," Injured characters are either wounded or seriously wounded. A character is considered seriously wounded if he has lost at least one point from all three of his physical characteristics. As soon as one of his physical characteristics is restored, no matter how, he is no longer seriously wounded. "),i(),t(789,"p"),e(790," Seriously wounded characters who have somehow avoided unconsciousness cannot move except to hobble or crawl along at 1.5 meters per combat round. They also lose their minor action in combat. "),i(),t(791,"p"),e(792," Wounded characters heal naturally and can also benefit from medical care. When characteristic points return from healing, players may choose which characteristic regains the points and may split healing between characteristics if they wish. "),i(),t(793,"h3",59),e(794,"Natural Healing"),i(),t(795,"p"),e(796," An injured character regains a number of characteristic points equal to his 1D6 + Endurance DM per day of full rest. If the character continues an active lifestyle he only heals a number of characteristic points equal to 1 + Endurance DM per day. Characters with a low Endurance DM (quite possibly from injury) may degrade (lose more characteristic points) over time if they are unlucky or cannot (or will not) rest. "),i(),t(797,"p"),e(798," A seriously wounded character only regains characteristic points equal to his Endurance DM per day of rest, which means that the character may never heal naturally and will even get worse if his Endurance DM is currently negative. "),i(),t(799,"h3",60),e(800,"Medical Treatment"),i(),t(801,"p"),e(802," Natural healing is not the only way to recover damage. Characters can also seek out medical treatment. "),i(),t(803,"dl")(804,"dt"),e(805,"First Aid"),i(),t(806,"dd"),e(807,"Applying first aid restores a number of characteristic points equal to twice the Effect of the "),t(808,"a",61),e(809,"Medicine"),i(),e(810," check. Points restored by first aid are divided as desired among all damaged physical characteristics. First aid must be applied within five minutes of the injuries being received to be fully effective. A character can still benefit from first aid up to an hour after their injury but they only receive a number of characteristic points equal to the Effect of the "),t(811,"a",61),e(812,"Medicine"),i(),e(813," check. Performing first aid on yourself is a Difficult (\u20132) task."),i(),t(814,"dt"),e(815,"Surgery"),i(),t(816,"dd"),e(817,"A character that is seriously wounded (after first aid has been applied) requires surgery. Surgery restores characteristic points just like first aid but if the check is failed the patient loses characteristic points equal to the Effect. Surgery requires a hospital or sickbay. Once one characteristic is back to its maximum level the patient can benefit from medical care. Surgery does not benefit characters that are not seriously wounded. Performing surgery on yourself is a Very Difficult (\u20134) task."),i(),t(818,"dt"),e(819,"Medical Care"),i(),t(820,"dd"),e(821,"Medical care restores 2 + the character's Endurance DM + the doctor's "),t(822,"a",61),e(823,"Medicine"),i(),e(824," skill in characteristic points per day, divided evenly among all damaged characteristics. Medical care requires a hospital or sickbay and for the patient to undergo full bed rest."),i()(),t(825,"h3",62),e(826,"Healing and Mental Characteristics"),i(),t(827,"p"),e(828," Other than Psionic Strength, characters may also suffer damage to their Intelligence or even their Education. Unless otherwise specified, each mental characteristic heals at the rate of one point per day. "),i(),t(829,"h2",63),e(830,"Vehicles in Personal Combat"),i(),t(831,"p"),e(832," Combat in and on vehicles is much the same as ordinary combat. The differences are as follows: "),i(),t(833,"ul")(834,"li"),e(835,"Unlike people, who are mobile and maneuverable, careful track must be kept of which way a vehicle is facing. Vehicle-mounted weapons \u2013 and armed passengers, to a lesser extent \u2013 are restricted to certain fire arcs."),i(),t(836,"li"),e(837,"Vehicles are considered to move on the driver's Initiative. The driver must spend a minor action every round to keep control of the vehicle under normal circumstances \u2013 a straight road or simple maneuvers \u2013 or a significant action to navigate obstacles, conduct evasion or pursuit, or dodge incoming fire."),i(),t(838,"li"),e(839,"Attackers gain a +1 DM to hit most vehicles because of their size."),i()(),t(840,"h3",64),e(841,"Closed and Open Vehicles"),i(),t(842,"p"),e(843," There are two main types of vehicles: open and closed. "),i(),t(844,"dl")(845,"dt"),e(846,"Closed Vehicles"),i(),t(847,"dd"),e(848,"Closed vehicles grant cover to the occupants \u2013 unless the description mentions otherwise civilian vehicles grant \xBD soft cover and military vehicles full hard cover. Only a few people in a closed vehicle can shoot out, depending on the number of windows or other firing ports and the internal space available. Unless the description mentions otherwise up to two people can fire into each arc from a civilian vehicle and one person in each arc in a military one."),i(),t(849,"dt"),e(850,"Open Vehicles"),i(),t(851,"dd"),e(852,"Open vehicles grant no cover to the passengers. Any passenger in an open vehicle can shoot (or otherwise attack) in any direction."),i()(),t(853,"h3",65),e(854,"Vehicle-Mounted Weapons"),i(),t(855,"p"),e(856," Weapons mounted on vehicles are limited in what directions they can fire. A weapon mounted in the front arc, for example, can only fire into a 90\xB0 area in front of the vehicle. Weapons in turrets can fire in any direction. "),i(),t(857,"h3",66),e(858,"Collisions"),i(),t(859,"p"),e(860," When a vehicle collides with something else everything takes damage. Roll 1D6 for every 10 kph of the vehicle's speed (round up). This is applied as damage directly to anything hit and, if the thing struck is solid enough, also to the ramming vehicle. Any unsecured passengers in a vehicle damaged in a collision take the same damage and, if possible, are thrown three meters for every 10 kph of speed. Secured passengers (those wearing seatbelts or something similar) are not thrown anywhere and take one quarter damage. "),i(),t(861,"h3",67),e(862,"Vehicular Actions"),i(),t(863,"p"),e(864," These are all significant actions that the driver of a vehicle can take when his turn in the initiative order arrives. "),i(),t(865,"h4"),e(866,"Evasive Action"),i(),t(867,"p"),e(868," Vehicles are not typically maneuverable enough to dodge as a reaction. Instead, the driver may declare that he is taking evasive action when his turn arrives. He makes a skill check (skill determined by vehicle) and the Effect acts as a \u2013DM to all attacks against the vehicle or its passengers. The Effect also acts as a \u2013DM to any attacks made from the vehicle as well. This lasts until the driver's next action. "),i(),t(869,"h4"),e(870,"Maneuvering"),i(),t(871,"p"),e(872," A driver or pilot can maneuver his vehicle without making a skill check. This allows the vehicle to avoid large or obvious obstacles, to get where it is going, to move out of one fire arc of an enemy vehicle and into a different one, or to change the fire arc that a single target is in. "),i(),t(873,"h4"),e(874,"Ram"),i(),t(875,"p"),e(876," Deliberately driving a vehicle into someone or something requires a significant action and a successful skill check (skill determined by vehicle). Rams are affected by dodging and evasive action as normal. The Referee may grant bonuses to a ram attempt or declare it automatically successful if the target is particularly large. "),i(),t(877,"h4"),e(878,"Stunt"),i(),t(879,"p"),e(880," With a significant action and a successful vehicle control check the driver or pilot of a vehicle can do pretty much anything it is possible to do in his vehicle \u2013 stand a car up on two wheels, perform stunning aerobatics in a jet plane, or skim a speedboat over a low sandbar. A stunt can be used to put a single target into one additional fire arc for one round, to set up some other skill check using the rules for task chains, to achieve something that would normally be difficult or impossible in your vehicle, to achieve up to three maneuver actions in one go, just to show off, or anything else you can imagine. "),i(),t(881,"h4"),e(882,"Weave"),i(),t(883,"p"),e(884," In an environment with many obstacles, such as an inner city or tight underground caverns, a driver or pilot may choose to weave his vehicle in and around the obstacles at high speed in order to evade pursuit. The driver chooses a weaving number, as low as one or as high as one per 20 km/h of speed (round up), and must then make a skill check (skill determined by vehicle) with the weaving number as a penalty on his roll. If he fails, he has woven into an obstacle and crashed. If he succeeds, any pursuers must choose a weave action on their turn and make their skill test at the same penalty with the same consequence for failure. Alternatively, they can choose to break off pursuit and either give up or try to reacquire the target later. "),i(),t(885,"h3",68),e(886,"Vehicle Damage"),i(),t(887,"p"),e(888," Vehicles have a Hull value and a Structure value, which measure the vehicle's structural integrity. When Hull is reduced to 0, the vehicle starts taking damage to its internal systems. When Structure is reduced to 0, the vehicle is reduced to scrap. Vehicles also suffer damage to onboard systems as they take damage. "),i(),t(889,"p"),e(890," To determine the effects of an attack on a vehicle, first determine how much damage the vehicle suffers as normal. Many vehicles will have one or more points of armor that reduces the damage. Consult the Vehicle Damage table to determine how many 'hits' the vehicle suffers. Each hit is then applied to a particular location on the vehicle. Double or Triple hits count as two or three hits on the same location. "),i(),t(891,"table",3)(892,"caption"),e(893,"Table: Vehicle Damage"),i(),t(894,"thead",4)(895,"tr")(896,"th"),e(897,"Damage"),i(),t(898,"th"),e(899,"Effect"),i()()(),t(900,"tr")(901,"th"),e(902,"0 or less"),i(),t(903,"td"),e(904,"No damage"),i()(),t(905,"tr")(906,"th"),e(907,"1\u20133"),i(),t(908,"td"),e(909,"Single Hit"),i()(),t(910,"tr")(911,"th"),e(912,"4\u20136"),i(),t(913,"td"),e(914,"Two Single Hits"),i()(),t(915,"tr")(916,"th"),e(917,"7\u20139"),i(),t(918,"td"),e(919,"Double Hit"),i()(),t(920,"tr")(921,"th"),e(922,"10\u201312"),i(),t(923,"td"),e(924,"Three Single Hits"),i()(),t(925,"tr")(926,"th"),e(927,"13\u201315"),i(),t(928,"td"),e(929,"Two Single Hits, Double Hit"),i()(),t(930,"tr")(931,"th"),e(932,"16\u201318"),i(),t(933,"td"),e(934,"Two Double Hits"),i()(),t(935,"tr")(936,"th"),e(937,"19\u201321"),i(),t(938,"td"),e(939,"Triple Hit"),i()(),t(940,"tr")(941,"th"),e(942,"22\u201324"),i(),t(943,"td"),e(944,"Triple Hit, Single Hit"),i()(),t(945,"tr")(946,"th"),e(947,"25\u201327"),i(),t(948,"td"),e(949,"Triple Hit, Double Hit"),i()(),t(950,"tr")(951,"th"),e(952,"28\u201330"),i(),t(953,"td"),e(954,"Triple Hit, Double Hit, Single Hit"),i()(),t(955,"tr")(956,"th"),e(957,"31\u201333"),i(),t(958,"td"),e(959,"Two Triple Hits"),i()(),t(960,"tr")(961,"th"),e(962,"For every extra three points"),i(),t(963,"td"),e(964,"+1 Single Hit"),i()(),t(965,"tr")(966,"th"),e(967,"For every extra six points"),i(),t(968,"td"),e(969,"+1 Double Hit"),i()()(),t(970,"table",3)(971,"caption"),e(972,"Table: Vehicle Hit Location"),i(),t(973,"thead",4)(974,"tr")(975,"th"),e(976,"2D6"),i(),t(977,"th"),e(978,"External Hit (Vehicle)"),i(),t(979,"th"),e(980,"Internal Hit (Vehicle)"),i(),t(981,"th"),e(982,"Robot or Drone"),i()()(),t(983,"tr")(984,"th"),e(985,"2"),i(),t(986,"td"),e(987,"Hull"),i(),t(988,"td"),e(989,"Structure"),i(),t(990,"td"),e(991,"Hull"),i()(),t(992,"tr")(993,"th"),e(994,"3"),i(),t(995,"td"),e(996,"Sensors"),i(),t(997,"td"),e(998,"Power Plant"),i(),t(999,"td"),e(1e3,"Power Plant"),i()(),t(1001,"tr")(1002,"th"),e(1003,"4"),i(),t(1004,"td"),e(1005,"Drive"),i(),t(1006,"td"),e(1007,"Power Plant"),i(),t(1008,"td"),e(1009,"Sensors"),i()(),t(1010,"tr")(1011,"th"),e(1012,"5"),i(),t(1013,"td"),e(1014,"Weapon"),i(),t(1015,"td"),e(1016,"Cargo"),i(),t(1017,"td"),e(1018,"Weapon or Limb"),i()(),t(1019,"tr")(1020,"th"),e(1021,"6"),i(),t(1022,"td"),e(1023,"Hull"),i(),t(1024,"td"),e(1025,"Structure"),i(),t(1026,"td"),e(1027,"Hull"),i()(),t(1028,"tr")(1029,"th"),e(1030,"7"),i(),t(1031,"td"),e(1032,"Armor"),i(),t(1033,"td"),e(1034,"Passengers"),i(),t(1035,"td"),e(1036,"Armor"),i()(),t(1037,"tr")(1038,"th"),e(1039,"8"),i(),t(1040,"td"),e(1041,"Hull"),i(),t(1042,"td"),e(1043,"Structure"),i(),t(1044,"td"),e(1045,"Hull"),i()(),t(1046,"tr")(1047,"th"),e(1048,"9"),i(),t(1049,"td"),e(1050,"Weapon"),i(),t(1051,"td"),e(1052,"Cargo"),i(),t(1053,"td"),e(1054,"Weapon or Limb"),i()(),t(1055,"tr")(1056,"th"),e(1057,"10"),i(),t(1058,"td"),e(1059,"Drive"),i(),t(1060,"td"),e(1061,"Computers"),i(),t(1062,"td"),e(1063,"Drive"),i()(),t(1064,"tr")(1065,"th"),e(1066,"11"),i(),t(1067,"td"),e(1068,"Sensors"),i(),t(1069,"td"),e(1070,"Cockpit"),i(),t(1071,"td"),e(1072,"Sensors"),i()(),t(1073,"tr")(1074,"th"),e(1075,"12"),i(),t(1076,"td"),e(1077,"Hull"),i(),t(1078,"td"),e(1079,"Cockpit"),i(),t(1080,"td"),e(1081,"Computer"),i()()(),t(1082,"h4"),e(1083,"Hull"),i(),t(1084,"p"),e(1085," Reduce the vehicle or drone's Hull by one. If a vehicle runs out of Hull, further Hull hits become hits on the same row of the Internal Damage table (if a vehicle) or Structure hits (if a robot or drone). "),i(),t(1086,"h4"),e(1087,"Structure"),i(),t(1088,"p"),e(1089," Reduce the vehicle or drone's Structure by one. If a vehicle runs out of Structure, it is destroyed. If the vehicle is destroyed by an attack that reduces it to a negative Structure score it explodes, doing 4D6 damage to everyone within six meters (including the occupants) and 2D6 damage to everyone within twelve meters. The occupants of a closed vehicle cannot dodge or dive for cover from this explosion but the occupants of an open vehicle can. "),i(),t(1090,"h4"),e(1091,"Armor"),i(),t(1092,"p"),e(1093," Reduce the vehicle's armor by one. "),i(),t(1094,"h4"),e(1095,"Drive"),i(),t(1096,"dl")(1097,"dt"),e(1098,"First Hit"),i(),t(1099,"dd"),e(1100,"Reduce movement by 10% and apply a \u20131 DM to all vehicle control skill checks."),i(),t(1101,"dt"),e(1102,"Second Hit"),i(),t(1103,"dd"),e(1104,"Reduce movement by 25% and apply a \u20132 DM to all vehicle control skill checks."),i(),t(1105,"dt"),e(1106,"Third Hit"),i(),t(1107,"dd"),e(1108,"Drive disabled."),i()(),t(1109,"p"),e(1110," Further drive hits count as Hull hits. "),i(),t(1111,"h4"),e(1112,"Weapon"),i(),t(1113,"p"),e(1114," Choose a weapon or device randomly for each hit. "),i(),t(1115,"dl")(1116,"dt"),e(1117,"First Hit"),i(),t(1118,"dd"),e(1119,"The weapon or device suffers a \u20132 DM to all checks related to its operation."),i(),t(1120,"dt"),e(1121,"Second Hit"),i(),t(1122,"dd"),e(1123,"The weapon or device is destroyed."),i()(),t(1124,"p"),e(1125," If no weapons remain to be destroyed, further hits on this location become Hull hits. "),i(),t(1126,"h4"),e(1127,"Sensors"),i(),t(1128,"dl")(1129,"dt"),e(1130,"First Hit"),i(),t(1131,"dd"),e(1132,"The vehicle or drone suffers a \u20132 DM to all Comms checks to use sensors. For drones and robots, this also applies to Recon checks."),i(),t(1133,"dt"),e(1134,"Second Hit"),i(),t(1135,"dd"),e(1136,"The sensors are destroyed, blinding the vehicle or drone."),i()(),t(1137,"p"),e(1138," Further Sensor hits count as Hull hits. "),i(),t(1139,"h4"),e(1140,"Power Plant"),i(),t(1141,"dl")(1142,"dt"),e(1143,"First Hit"),i(),t(1144,"dd"),e(1145,"The vehicle or drone loses one round's worth of actions."),i(),t(1146,"dt"),e(1147,"Second Hit"),i(),t(1148,"dd"),e(1149,"The vehicle or drone's movement is reduced by 50%."),i(),t(1150,"dt"),e(1151,"Third Hit"),i(),t(1152,"dd"),e(1153,"The power plant is destroyed, disabling the vehicle and inflicting 1D6 Hull hits on it."),i()(),t(1154,"h4"),e(1155,"Limb"),i(),t(1156,"p"),e(1157," Choose a limb randomly for each limb hit. "),i(),t(1158,"dl")(1159,"dt"),e(1160,"First Hit"),i(),t(1161,"dd"),e(1162,"The limb suffers a \u20132 DM to all checks related to its operation."),i(),t(1163,"dt"),e(1164,"Second Hit"),i(),t(1165,"dd"),e(1166,"The limb is destroyed."),i()(),t(1167,"p"),e(1168," Further Limb hits count as Hull hits. "),i(),t(1169,"h4"),e(1170,"Passengers"),i(),t(1171,"p"),e(1172," Choose a passenger randomly for any passenger hit. The passenger takes damage equal to the damage inflicted on the vehicle. If all the passengers are dead, further passenger hits become Structure hits. "),i(),t(1173,"h4"),e(1174,"Cargo"),i(),t(1175,"p"),e(1176," Any cargo present is hit and may be destroyed. If no cargo remains, further cargo hits become Structure hits. "),i(),t(1177,"h4"),e(1178,"Cockpit"),i(),t(1179,"p"),e(1180," The pilot of the vehicle is hit, and takes damage equal to the damage inflicted on the vehicle. If the pilot is dead, further pilot hits become Structure hits. "),i(),t(1181,"h4"),e(1182,"Computer"),i(),t(1183,"dl")(1184,"dt"),e(1185,"First Hit"),i(),t(1186,"dd"),e(1187,"The vehicle's computer system is disabled. A drone or robot with a disabled computer system shuts down for 1D6 rounds."),i(),t(1188,"dt"),e(1189,"Second Hit"),i(),t(1190,"dd"),e(1191,"The vehicle's computer system is destroyed. A drone or robot with no computer system is completely disabled."),i()(),t(1192,"p"),e(1193," Further Computer hits count as Structure hits. "),i(),t(1194,"h3"),e(1195,"Repairs"),i(),t(1196,"p"),e(1197," Damage to a vehicle or drone falls into three categories \u2013 System Damage, Hull Damage, and Structure Damage. "),i(),t(1198,"dl")(1199,"dt"),e(1200,"System Damage"),i(),t(1201,"dd")(1202,"p"),e(1203," A damaged system can be jury-rigged back to functioning, but it will stop functioning again after 1D6 hours. Repairing a damaged system requires not only an Average skill check (using the appropriate skill as determined by the Referee, such as "),t(1204,"a",69),e(1205,"Electronics"),i(),e(1206,", "),t(1207,"a",70),e(1208,"Engineering"),i(),e(1209,", "),t(1210,"a",71),e(1211,"Gravitics"),i(),e(1212,", "),t(1213,"a",72),e(1214,"Mechanics"),i(),e(1215,") taking 1\u20136 hours but also a source of spare parts. The spare parts can come from a scrap yard, a workshop, systems on another vehicle, or can be taken from other systems on the same vehicle. When taking spare parts from other vehicle systems, each 'hit' of damage provides enough spare parts to make a single repair check. "),i(),t(1216,"p"),e(1217," The Passengers and Cockpit systems cannot take hits to provide spare parts, although cybernetic parts might be able to provide enough spare material to repair minor damage. "),i(),t(1218,"p"),e(1219," A destroyed system costs 2D6x10% of its original cost to repair, and cannot be repaired using spare parts. It requires a full workshop and specialist materials. "),i()(),t(1220,"dt"),e(1221,"Hull Damage"),i(),t(1222,"dd"),e(1223,"Hull damage can be repaired with a "),t(1224,"a",72),e(1225,"Mechanics"),i(),e(1226," check taking 1\u20136 hours and consumes one 'hit' of spare parts."),i(),t(1227,"dt"),e(1228,"Structure Damage"),i(),t(1229,"dd"),e(1230,"Structure damage can only be repaired in a workshop and requires 10\u201360 hours per point of damage. It costs 20% of the base cost of the vehicle per point repaired. No skill check is required."),i()(),t(1231,"h2",73),e(1232,"Ground Force Weaponry against Starship-scale Targets"),i(),t(1233,"p"),e(1234," Gaining a DM +4 bonus to hit anything on the starship-scale, ground force weaponry must divide its damage by 50 before comparing it to a starship-scale target's armor. Because a single weapon will, obviously, be unable to punch though armor it is possible for multiple weapons to all target the starship simultaneously, and the cumulative effect can inflict damage. Every additional ground weapon beyond the first can add half its damage dice to the total before dividing the total by 50 in order to calculate damage. "),i())},dependencies:[d],encapsulation:2})};export{m as PersonalCombatComponent};
